/// <reference types="react" />
import { ComponentShieldAuthProps, ComponentShieldBaseProps, ComponentShieldRBACProps } from '../types/Component';
/**
 * üõ°Ô∏è A Shield for your Components, it handles when a component shows or not based on the auth state or the user's role
 *
 * @param ComponentShieldProps {@link ComponentShieldProps | see here}
 * @returns Children Components | {@link JSX.Element}
 *
 *
 * @example
 * ```tsx
 *  // Basic Example
 *  <ComponentShield showIf={true}>
 *    <p>You are authorized</p>
 *  </ComponentShield>
 *
 *  // Fallback Example
 *  <ComponentShield showIf={false} fallback={<p>You are unauthorized</p>}>
 *    <p>You are authorized</p>
 *  </ComponentShield>
 *
 *  // RBAC Example
 *  <ComponentShield RBAC showForRole="ADMIN" userRole="ADMIN">
 *    <p>You are an ADMIN</p>
 *  </ComponentShield>
 * ```
 *
 * @packageDocumentation
 */
export declare function ComponentShield(props: ComponentShieldBaseProps): JSX.Element;
export declare function ComponentShield(props: ComponentShieldAuthProps): JSX.Element;
export declare function ComponentShield(props: ComponentShieldRBACProps): JSX.Element;
