import { ReactNode } from 'react';
import { NextShieldProps } from '../types/props';
/**
 * ðŸ˜‰ The shield that every Next.js app needs
 *
 * @typeParam NextShieldProps - {@link NextShieldProps | see here}
 * @returns NextShield Component
 *
 * @example
 * ```tsx
 * import { Loading } from '@/components/routes/Loading'
 * import { useAuth } from '@/hooks/auth'
 *
 * export default function MyApp({ Component, pageProps }: AppProps) {
 *  const { isAuth, isLoading } = useAuth()
 *  const router = useRouter()
 *
 *  return (
 *    <NextShield
 *      isAuth={isAuth}
 *      isLoading={isLoading}
 *      router={router}
 *      privateRoutes={['/protected']}
 *      publicRoutes={['/']}
 *      hybridRoutes={['/products/[slug]']}
 *      LoadingComponent={<Loading />}
 *    >
 *      <Component {...pageProps} />
 *    </NextShield>
 *   )
 * }
 *
 * ```
 * @packageDocumentation
 */
export declare function NextShield<PrivateRoutesList extends string[], PublicRoutesList extends string[]>({ isAuth, isLoading, router: { pathname, replace }, loginRoute, accessRoute, privateRoutes, publicRoutes, hybridRoutes, LoadingComponent, RBAC, userRole, children, }: NextShieldProps<PrivateRoutesList, PublicRoutesList> & {
    children: ReactNode;
}): JSX.Element;
